import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {getAllGroups, getContactById, getGroupById} from "../../services/contactService";

const ViewContact = () => {
    const {contactId} = useParams();
    const [contact, setContact] = useState({});

    useEffect(() => {
        const fetchContactData = async () => {
            const {data: contactData} = await getContactById(contactId);
            const {data: groupData} = await getGroupById(contactData.group);

            setContact(contactData);
        }
        fetchContactData()
    }, [])
    return (
        <>
            <section>
                <div className="container">
                    <div className="row">
                        <div className="col-md-4">
                            {/*<img src="" alt="" className="img-fluid rounded"/>*/}
                        </div>
                        <div className="col-md-8">
                            <ul className="list-group">
                                <li className="list-group-item list-group-item-dark">
                                    Name : {" "}
                                    <span className="fw-bold">

                                    </span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Mobile: {" "}
                                    <span className="fw-bold"></span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Email: {" "}
                                    <span className="fw-bold"></span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Job: {" "}
                                    <span className="fw-bold"></span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Group: {" "}
                                    <span className="fw-bold"></span>
                                </li>
                            </ul>

                        </div>
                    </div>
                </div>
            </section>
        </>
    )
}
export default ViewContact;
