import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {getContactById, getGroupById} from "../../services/contactService";

const ViewContact = () => {
    const {contactId} = useParams();
    const [localContact, setLocalContact] = useState({contact});
    useEffect(() => {
        const fetchContactData = async () => {
            try {
                const {data: contactData} = await getContactById(contactId);
                const {data: groupData} = await getGroupById(contactId);

                setLocalContact({
                    ...localContact,
                    contact: contactData,
                    group: groupData.name
                })
                console.log(localContact);
            } catch (err) {
                console.log(err.message);
            }

        };
        fetchContactData()
    }, [])
    return (
        <>
            <section>
                <div className="container">
                    <div className="row">
                        <div className="col-md-4">
                            <img src={localContact.photo} alt="" className="img-fluid rounded"/>
                        </div>
                        <div className="col-md-8">
                            <ul className="list-group">
                                <li className="list-group-item list-group-item-dark">
                                    Name : {" "}
                                    <span className="fw-bold">
                                        {localContact.fullName}
                                    </span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Mobile: {" "}
                                    <span className="fw-bold">
                                        {localContact.mobile}
                                    </span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Email: {" "}
                                    <span className="fw-bold">
                                        {localContact.email}
                                    </span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Job: {" "}
                                    <span className="fw-bold">
                                        {localContact.job}
                                    </span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Group: {" "}
                                    <span className="fw-bold">
                                        {localContact.group}
                                    </span>
                                </li>
                            </ul>

                        </div>
                    </div>
                </div>
            </section>
        </>
    )
}
export default ViewContact;
