import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {getAllGroups, getContactById, getGroupById} from "../../services/contactService";

const ViewContact = () => {
    const {contactId} = useParams();
    const [contact, setContact] = useState({});
    const [groupName, setGroupName] = useState('')

    useEffect(() => {
        const fetchContactData = async () => {
            try {
                const {data: contactData} = await getContactById(contactId);
                const {data: groupData} = await getGroupById(contactData.group);

                setContact({
                    ...contactData,
                    contact: contactData,
                    group: groupData
                });
                // setGroupName(groupData.name)
                // console.log(groupName);
            } catch (err) {
                console.log(err.message);
            }
        }
        fetchContactData().then()
    }, [])
    return (
        <>
            <section>
                <div className="container">
                    <div className="row">
                        <div className="col-md-4">
                            <img src={contact.photo} alt="" className="img-fluid rounded"/>
                        </div>
                        <div className="col-md-8">
                            <ul className="list-group">
                                <li className="list-group-item list-group-item-dark">
                                    Name : {" "}
                                    <span className="fw-bold">
                                        {contact.fullName}
                                    </span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Mobile: {" "}
                                    <span className="fw-bold">
                                        {contact.mobile}
                                    </span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Email: {" "}
                                    <span className="fw-bold">
                                        {contact.email}
                                    </span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Job: {" "}
                                    <span className="fw-bold">
                                        {contact.job}
                                    </span>
                                </li>
                                <li className="list-group-item list-group-item-dark">
                                    Group: {" "}
                                    <span className="fw-bold">
                                        {contact.group}
                                    </span>
                                </li>
                            </ul>

                        </div>
                    </div>
                </div>
            </section>
        </>
    )
}
export default ViewContact;
